import re 
print("Part One: " + str(sum(list(map(int, re.findall(r'\d+', l)))[0] for l in open('day02.txt', 'r').readlines() if all(int(item) <= 12 for item in map(int, re.findall(r'(\d+) red', l))) and all(int(item) <= 13 for item in map(int, re.findall(r'(\d+) green', l))) and all(int(item) <= 14 for item in map(int, re.findall(r'(\d+) blue', l)))))+ '\n' + "Part Two: " + str(sum(max(list(map(int, re.findall(r'(\d+) red', l)))) * max(list(map(int, re.findall(r'(\d+) green', l)))) * max(list(map(int, re.findall(r'(\d+) blue', l)))) for l in open('day02.txt', 'r').readlines())))